import React from 'react';
import { useParams } from 'react-router-dom';

import { useQuery, gql } from '@apollo/client';

const GET_MOVIE = gql`
  query getMovie($id: Int!) {
    movie(id: $id) {
      id
      title
      rating
      language
      description_intro
      medium_cover_image
    }
  }
`;

function Detail() {
  const id = useParams();
  const { loading, error, data } = useQuery(GET_MOVIE, { variables: { id } });
  return (
    <div>
