import React from 'react';
import { gql, useQuery } from '@apollo/client';

const GET_MOVIES = gql`
  query {
    movies {
      id
      medium_cover_image
    }
  }
`;

function Home() {
  const { loading, error, data } = useQuery(GET_MOVIES);
  console.log(loading, error, data);
  if (loading) {
    return 'Loading...';
  }
  if (data && data.movies) {
    return (
      <div>
        <p>home</p>
        {data.map((dat) => {
          return (
            <div>
              <p>{dat.id}</p>
              <img resource={dat.medium_cover_image} />
            </div>
          );
        })}
      </div>
    );
  }
}

export default Home;
